import React , { useState, useEffect} from "react";
import { getProducts } from "../services";
import Loading from "./Loading";

const ListProducts = (/* {products} */) => {
    //funcion hoock
     const [isLoading, setIsLoading]= useState(true)
    const [products, setProducts] = useState([])

    useEffect( () => {
        async function loadProducts () {
            const response = await getProducts()

            if (response.status === 200){
                setProducts(response.data.products)
            } 

            setIsLoading(false)
        }

        loadProducts()
    },[]) 

    if (isLoading){
        return <Loading/>
    }

    if (!products.length){
        return <h2 className="title has-text-centered">You don't have products</h2> 
    }
    
    return (
        <div className="row">
            {products.map((item,index)=>(
                
                <div key= {index} className="col mb-4">
                    <div className="card" style={{width: "18rem"}}>
                        <img src={item.imgUrl} className="card"  alt="" />
                        <div className="car-body p-2">
                        <h5 className="card-text" style={{textAlign:'center'}}>{item.name}</h5>
                        <hr/>
                        <p style={{textAlign:'center'}}>Descripci√≥n: {item.description} </p>
                        </div>
                    </div>
                 
                </div>   
                   
            ))}                
        </div>
         
    )
}

export default ListProducts
